#include <bits/stdc++.h>
using namespace std;
#include "helper.h"

typedef pair<int, int> P;
typedef long long int LL;
#define arr array
#define PB push_back
#define MP make_pair
#define X first
#define Y second

class Solution {
public:
    unordered_set<string> d;
    unordered_map<string, bool> vis;
    bool solve(string s)
    {
        if(vis.count(s))
            return vis[s];
        if(d.count(s))
            return vis[s] = 1;

        int n = s.size();
        for(int i = 1; i <= n-1; i++)
        {
            string a = s.substr(0, i), b = s.substr(i, n-i);
            if(!vis.count(a))
                vis[a] = solve(a);

            if(!vis.count(b))
                vis[b] = solve(b);

            if(vis[a] && vis[b])
                return vis[s] = 1;
        }

        return vis[s] = 0;
    }
    bool wordBreak(string s, vector<string>& wordDict)
    {
        d = {wordDict.begin(), wordDict.end()};
        return solve(s);
    }
};

